css:
.print-format th {
    background-color: #fff !important;
}

.table > tbody > tr > td {
  border-top: 0px !important;
}

.table > tbody > tr > th {
  border-bottom: 1px solid #d1d8dd !important;
}

.table > tbody > tr:first-child {
  border-right: 1px solid #d1d8dd !important;
  border-left: 1px solid #d1d8dd !important;
}

.padded-line {
  padding-right: 6px;
}

.total-line {
  border-bottom: 1px solid #d1d8dd !important;
}

.top-row, .terms {
  margin-top: 25px;
}

.strg {
  font-size: 12pt;
}

.very-strg {
  font-size: 14pt;
}

.print-format {
  font-family: "Lato";
  font-size: 11pt;
  padding: 15px;
}

.pg-brk {
 page-break-before: always !important;
}


.tbl-head-top {
  margin-top: 0px;
}
